import React from 'react';
import './App.css';
import salfordlogo from '../src/images/salfordlogo.png'
// import facepic from '../src/images/facepic.png'
// import universitybuilding from '../src/images/universitybuilding.jpg'

class Test extends React.Component {
  constructor(){
         super();

         this.state = {
              green: true
         }
    }

    changeColor(){
        this.setState({green: !this.state.green})
    }

    render(){
        let btn_class = this.state.green ? "greenButton" : "redButton";

        return (
             <div>
                 <button className={btn_class}
                         onClick={this.changeColor.bind(this)}>
                           Button
                  </button>
             </div>
        )
    }
}




class App extends React.Component {
  render(){
  return (
    <div className="App">
        <Header />
        <Student />
        <Test />
    </div>
  );
}
}


class Header extends React.Component {
  render(){
    return(
      <div className="heading">
        {/* <img id="buildingpic" src={universitybuilding} alt="buildingpic" /> */}
        <img id="logoimg" src={salfordlogo} alt="logoimg" />
        <h1>University Of Salford</h1>
        <h2>Paper Digital Certificate</h2>
        <h3>Student Number: 007AW643</h3>

      </div>

    )
  }

}

class Student extends React.Component {
  render(){
    return(
      <div className="studentinfo">
        {/* <img id="fcepic" src={facepic} alt="facepicture" /> */}
        <h2>Name: Tom Franks</h2>
        <h2>Address: 24 Melonway, Cumbria, England, CA14 4TY </h2>
        <h2>Course: BA(hons) Business Studies</h2>
        <h2>Degree Classification:  2:1</h2>
        <h2>Dates: 2010-2014</h2>

      </div>

    )
  }
}


export default App;
